template.value:
var mNext = ""
private val mEmpty = ListEmptyData()
val mType = BindingType.create(::class.java, R.layout.)
val mAdapter = EndlessAdapter(mType, ItemTypes.EMPTY)

override fun onCreate(savedInstanceState: Bundle?) {
    super.onCreate(savedInstanceState)
    mBinding.recycler.adapter = mAdapter
    mAdapter.setOnLoadMoreListener {
        getData(false)
    }
    mBinding.refresh.setOnRefreshListener {
        getData(true)
    }
    getData(true)
}


fun getData(isRefresh: Boolean) {
    mNext = if (isRefresh) "" else mNext
    API.area().(mNext).doOnComplete { mBinding.refresh.isRefreshing = false }
        .lifecycle(this).subscribe {
        mNext = it.data.next
        Utils.setDataList(mAdapter, it.data.items, isRefresh, it.data.hasMore, mEmpty)
    }
}

companion object {
        fun start(context: Context){
        context.startActivity(Intent(context, ::class.java))
        }
    }
